import React from "react";

import "./View.scss";
const changeSexId = (sexId) => {
  let sex = "Nam";
  if (sexId === "2") sex = "Nữ";
  return sex;
};
const changeVoiceId = (voiceId) => {
  let voice = "Miền Bắc";
  if (voiceId === "2") voice = "Miền Trung";
  else if (voiceId === "3") voice = "Miền Nam";
  return voice;
};

const changeLevelId = (levelId) => {
  let level = "";
  if (levelId === "1") level = "Sinh viên";
  else if (levelId === "2") level = "Gia sư";
  else if (levelId === "3") level = "Giáo viên";

  return level;
};
function clear() {
  let input_search_box = document.getElementById("input_jasu_id");

  if (input_search_box.value.trim() !== "") {
    input_search_box.value = "";
  }
}

function search_btn() {
  let input_search_box = document.getElementById("input_jasu_id");
  input_search_box.value = input_search_box.value.trim();
  console.log(input_search_box.value);
}

function search_enter(event) {
  if (event.keyCode === 13) {
    search_btn();
  }
}
const FindJasuView = ({ userList }) => {
  return (
    <div>
      <div className="findJasu">
        <div className="topNav" id="topNavId">
          <input
            type="text"
            onKeyUp={search_enter}
            className="input-txt"
            id="input_jasu_id"
            placeholder="Tìm kiếm theo tên, phone, email, facebook, ..."
          />
          <button className="btn clr" onClick={clear}>
            <i className="bx bx-x  bx-sm"></i>
          </button>
          <button className="btn sch" onClick={search_btn}>
            <i className="bx bx-search  bx-sm"></i>
          </button>
        </div>
      </div>
      <ul className="jasu-lists">
        {userList.map((item, index) => (
          <div key={index}>
            <li key={index}>
              <div className="userImg">
                <img src={item.img} alt={item.name} />
              </div>
              <div className="userInfo" style={{ position: "relative" }}>
                <p>Họ và tên: {item.name}</p>
                <p>Giới tính: {changeSexId(item.sex)}</p>
                <p>Giọng : {changeVoiceId(item.voice_region)}</p>
                <p>
                  CV :{" "}
                  <a href={item.cv} style={{ color: "blue" }}>
                    {item.cv}
                  </a>
                </p>
                <p>Trình độ : {changeLevelId(item.level)}</p>
              </div>
            </li>
          </div>
        ))}
      </ul>
    </div>
  );
};
export default FindJasuView;
