import React, { Component } from 'react'
import './Content.scss'


const changeSexId = (sexId) => {
    let sex = 'Nam';
    if (sexId === '2') sex = 'Nữ';
    return sex;
}
const changeVoiceId = (voiceId) => {
    let voice = 'Miền Bắc';
    if (voiceId === '2') voice = 'Miền Trung';
    else if (voiceId === '3') voice = 'Miền Nam';
    return voice
}

const changeLevelId = (levelId) => {
    let level = '';
    if (levelId === '1') level = 'Sinh viên'
    else if (levelId === '2') level = 'Gia sư';
    else if (levelId === '3') level = 'Giáo viên';

    return level;
}

const changeNameId = (id, userList)=>{
    id = id.replace(" ", "");
    let idList = id.split(", ");
    console.log(idList)
    let nameArray = [];
    function checkId(id){
        if(idList.includes(item.id)){
            return item.name;
        }
    }
    nameArray = userList.map((item, index) => checkId(item.id))

    console.log(nameArray);
}

const Content = ({ type, userList, classroomList }) => {
    if (type === 'classList') {
        return (
            <div>chuaw cos gif</div>
        )
    }
    else if (type === 'classControl') {
        return(
            <ul>
                {
                    classroomList.map((item, index) =>
                        <div key={index}>
                            <li key={index}>
                                <p>Giáo viên: {item.id_teachers}</p>
                                <p>Học sinh: {item.id_students}</p>
                                <p> </p>
                            </li>
                        </div>
                    )
                }
            </ul>
        )
    }
    else if (type === 'jasuList') {
        return (
            <ul className="jasu-lists">
                {
                    userList.map((item, index) =>
                        <div key={index}>
                            <li key={index}>
                                <div className="userImg">
                                    <img src={item.img} alt={item.name} />
                                </div>
                                <div style={{ position: 'relative', }}>
                                    <p>Họ và tên: {item.name}</p>
                                    <p>Giới tính: {changeSexId(item.sex)}</p>
                                    <p>Tông giọng: {changeVoiceId(item.voice_region)}</p>
                                    <p>Sđt: {item.phone}</p>
                                    <p>Trình độ: {changeLevelId(item.level)}</p>
                                </div>
                            </li>
                        </div>
                    )
                }
            </ul>
        )
    }
    else if (type === 'userControl') {
        return (
            <ul>
                {
                    userList.map((item, index) =>
                        <div key={index}>
                            <li key={index}>
                                <div className="userImg">
                                    <img src={item.img} alt={item.name} />
                                </div>
                                <div style={{ position: 'relative'}}>
                                    <p>Họ và tên: {item.name}</p>
                                    <p>Giới tính: {changeSexId(item.sex)}</p>
                                    <p>Tông giọng: {changeVoiceId(item.voice_region)}</p>
                                    <p>Sđt: {item.phone}</p>
                                    <p>Trình độ: {changeLevelId(item.level)}</p>
                                </div>
                            </li>
                        </div>
                    )
                }
            </ul>
        )
    }

}
export default Content;
