import React, { Component } from 'react'
import './UserLayout.scss'
import UserService from '../../services/UserService';
import { render } from '@testing-library/react';


const changeSexId = (sexId) => {
  let sex = 'Nam';

  if (sexId === '2') sex = 'Nữ';

  return sex;
}
const changeVoiceId = (voiceId) => {
  let voice = 'Miền Bắc';

  if (voiceId === '2') voice = 'Miền Trung';
  else if (voiceId === '3') voice = 'Miền Nam';
  return voice
}

const changeLevelId = (levelId) => {
  let level = '';
  if (levelId === '1') level = 'Sinh viên'
  else if (levelId === '2') level = 'Gia sư';
  else if (levelId === '3') level = 'Giáo viên';

  return level;

}
class UserControl extends Component {

  constructor(props) {
    super(props)
    this.state = {
      users: []
    }
  }
  componentDidMount() {
    UserService.getUsers().then((res) => {
      this.setState({ users: res.data })
    });
  }
  render() {
    const usersRoot = this.state.users;
    var usersTemp = usersRoot;

    function getList(userList) {
      return (
      <ul className="user-lists">
        {
          userList.map((user, index) =>
            <div key={index}>
              <li key={index}>
                <div className="userImg">
                  <img src={user.img} alt={user.name} />
                </div>
                <div style={{ position: 'relative', }}>
                  <p>Họ và tên: {user.name}</p>
                  <p>Giới tính: {changeSexId(user.sex)}</p>
                  <p>Tông giọng: {changeVoiceId(user.voice_region)}</p>
                  <p>Sđt: {user.phone}</p>
                  <p>Trình độ: {changeLevelId(user.level)}</p>
                </div>
              </li>
            </div>

          )
        }
      </ul>)
    }

    function clear() {
      document.getElementById("admin_input_txt_id").value = "";
    }

    function search_btn() {
      let input_search_box = document.getElementById("admin_input_txt_id").value;
      input_search_box = input_search_box.trim();
      if (input_search_box !== "") {
        // 
        usersTemp = [];
        console.log(input_search_box);
        document.getElementById('findJasu_Id')
      }
      else {
        usersTemp = usersRoot;
      }

    }

    function search_enter(event) {
      if (event.keyCode === 13) {
        search_btn();
      }
    }
    return (
      <div className="findJasu" id='findJasu_Id'>
        <div className="admin topNav">
          <input
            type="text"
            onKeyUp={search_enter}
            className="admin input-txt"
            id="admin_input_txt_id"
            placeholder="Tìm kiếm theo tên, phone, email, facebook, ..." />
          <button className="admin btn clr" onClick={clear}>
            <i className='bx bx-x  bx-sm'></i>
          </button>
          <button className="admin btn sch" onClick={search_btn}>
            <i className='bx bx-search  bx-sm'></i>
          </button>
        </div>

      </div>
    )
  }
}

export default UserControl;
