import React, { Component } from 'react'
import './Content.scss'


const changeSexId = (sexId) => {
    let sex = 'Nam';
    if (sexId === '2') sex = 'Nữ';
    return sex;
}
const changeVoiceId = (voiceId) => {
    let voice = 'Miền Bắc';
    if (voiceId === '2') voice = 'Miền Trung';
    else if (voiceId === '3') voice = 'Miền Nam';
    return voice
}

const changeLevelId = (levelId) => {
    let level = '';
    if (levelId === '1') level = 'Sinh viên'
    else if (levelId === '2') level = 'Gia sư';
    else if (levelId === '3') level = 'Giáo viên';

    return level;

}

const Content = ({ type, list }) => {
    console.log(list);
    if (type === 'classList') {
        return (
            <div>chuaw cos gif</div>
        )
    }
    else if (type === 'jasuList') {
        return (
            <ul className="jasu-lists">
                {
                    list.map((user, index) =>
                        <div key={index}>
                            <li key={index}>
                                <div className="userImg">
                                    <img src={user.img} alt={user.name} />
                                </div>
                                <div style={{ position: 'relative', }}>
                                    <p>Họ và tên: {user.name}</p>
                                    <p>Giới tính: {changeSexId(user.sex)}</p>
                                    <p>Tông giọng: {changeVoiceId(user.voice_region)}</p>
                                    <p>Sđt: {user.phone}</p>
                                    <p>Trình độ: {changeLevelId(user.level)}</p>
                                </div>
                            </li>
                        </div>
                    )
                }
            </ul>
        )
    }
    else if (type === 'classControl') {

    }
    else if (type === 'userControl') {
        return (
            <ul>
                {
                    list.map((user, index) =>
                        <div key={index}>
                            <li key={index}>
                                <div className="userImg">
                                    <img src={user.img} alt={user.name} />
                                </div>
                                <div style={{ position: 'relative'}}>
                                    <p>Họ và tên: {user.name}</p>
                                    <p>Giới tính: {changeSexId(user.sex)}</p>
                                    <p>Tông giọng: {changeVoiceId(user.voice_region)}</p>
                                    <p>Sđt: {user.phone}</p>
                                    <p>Trình độ: {changeLevelId(user.level)}</p>
                                </div>
                            </li>
                        </div>
                    )
                }
            </ul>
        )
    }

}
export default Content;
