import React, { Component } from 'react';
import UserDTOService from '../../services/UserDTOService';
import TopNav from '../../components/topnav/TopNav';
import FindJasuView from '../../components/view/FindJasuView';



class UserControl extends Component {

  constructor(props) {
    super(props)
    this.state = {
      userDTOs: [],
      userSearch: []
    }
  }
  componentDidMount() {
    UserDTOService.getUsers().then((res) => {
      this.setState({
        userDTOs: res.data
      })
    });
  }
  render() {

    const search = () => {
      let searchInput = document.getElementById('topNavId').value;
      console.log(searchInput)
      if (searchInput.trim()) {
        var result = [];
        UserDTOService.searchUser(searchInput).then((res) => {
          this.setState({
            userSearch: res.data
          })
        });
        console.log(this.state.userSearch);
        return result;
      }
      else{
        return this.state.userDTOs;
      }
    }

    return (

      <div className='findJasu'>
        <TopNav onKeyUp={search} />
        
        <FindJasuView userList={this.state.userDTOs} />
      </div>
    )
  }
}

export default UserControl;
