import React, { Component } from 'react'
import UserService from '../services/UserService'
const changeSexId = (sexId) => {
    let sex = 'Nam';

    if (sexId === '2') sex = 'Nữ';

    return sex;
}
const changeVoiceId = (voiceId) => {
    let voice = 'Miền Bắc';

    if (voiceId === '2') voice = 'Miền Trung';
    else if (voiceId === '3') voice = 'Miền Nam';

    return voice
}

const changeLevelId = (levelId) => {
    let level = 'Sinh viên';

    if (levelId === '2') level = 'Gia sư';
    else if (levelId === '3') level = 'Giáo viên';

    return level;

}
export default class ListUserComponent extends Component {
    constructor(props) {
        super(props)
        this.state = {
            users: []
        }
    }
    componentDidMount() {
        UserService.getUsers().then((res) => {
            this.setState({ users: res.data })
        });
    }
    render() {
        return (
            <div>
                <ul>
                    {
                    this.state.users.map((user, index) =>
                        <li key = {index}>
                        <div style = {{position: 'relative',}}>
                            <p>Họ và tên: {user.name}</p> 
                            <p>Giới tính: {changeSexId(user.sex)}</p>
                            <p>Tông giọng: {changeVoiceId(user.voice_tone)}</p>
                            <p>Sđt: {user.phone}</p>
                            <p>Trình độ: {changeLevelId(user.level)}</p>
                            <p>Trình độ: {changeLevelId(user.level)}</p>
                            <p>Trình độ: {changeLevelId(user.level)}</p>
                        </div>
                        <p style={{position: 'absolute'}}>dáds</p>
                        </li>
                    )
                    }
                </ul>
            </div>
        )
    }
}
