import React, { Component } from 'react';
import UserDTOService from '../../services/UserDTOService';
import FindJasuView from '../../components/view/FindJasuView';
import './components/pages/TopNav.scss' 
function clear() {
  let input_search_box = document.getElementById("input_jasu_id");

  if (input_search_box.value.trim() !== "") {
    input_search_box.value = "";
  }
}

function search_btn() {
  let input_search_box = document.getElementById("input_jasu_id");
  input_search_box.value = input_search_box.value.trim();
  console.log(input_search_box.value);
}

function search_enter(event) {
  if (event.keyCode === 13) {
    search_btn();
  }
}

class UserControl extends Component {
  
  constructor(props) {
    super(props)
    this.state = {
      userDTOs: []
    }
  }
  componentDidMount() {
    UserDTOService.getUsers().then((res) => {
      this.setState({
        userDTOs: res.data
      })
    });
    
  }
  render() {
    return (
      
      <div className='findJasu'>
        <div className="topNav" id='topNavId'>
          <input
            type="text"
            onKeyUp={search_enter}
            className="input-txt"
            id="input_jasu_id"
            placeholder="Tìm kiếm theo tên, phone, email, facebook, ..." />
          <button className="btn clr" onClick={clear}>
            <i className='bx bx-x  bx-sm'></i>
          </button>
          <button className="btn sch" onClick={search_btn}>
            <i className='bx bx-search  bx-sm'></i>
          </button>
        </div>
        <FindJasuView userList={this.state.userDTOs}/>
      </div>
    )
  }
}

export default UserControl;
