import React from 'react'
import './View.scss'

const changeSexId = (sexId) => {
    let sex = 'Nam';
    if (sexId === '2') sex = 'Nữ';
    return sex;
}
const changeVoiceId = (voiceId) => {
    let voice = 'Miền Bắc';
    if (voiceId === '2') voice = 'Miền Trung';
    else if (voiceId === '3') voice = 'Miền Nam';
    return voice
}

const changeLevelId = (levelId) => {
    let level = '';
    if (levelId === '1') level = 'Sinh viên'
    else if (levelId === '2') level = 'Gia sư';
    else if (levelId === '3') level = 'Giáo viên';

    return level;
}
const userControlView = ({ userList }) => {
    return (
        <div>
            <div className="topNav" id="topNavId">
        <input
          type="text"
          onChange={(event) => {
            setSearchTerm(event.target.value);
          }}
          className="input-txt"
          id="input_jasu_id"
          placeholder="Tìm kiếm theo tên, phone, email, facebook, ..."
        />
      </div>
            <ul>
            {
                userList.map((item, index) =>
                    <div key={index}>
                        <li key={index}>
                            <div className="userImg">
                                <img src={item.img} alt={item.name} />
                            </div>
                            <div className='_in4' style={{ position: 'relative' }}>
                                <p>Họ và tên: {item.name}</p>
                                <p>Age: {item.age}</p>
                                <p>Giới tính: {changeSexId(item.sex)}</p>
                                <p>Add: {item.address}</p>
                                <p>Sđt: {item.phone}</p>
                                <a href={item.cv}>CV: {item.cv}</a>
                                <p>Trạng thái kích hoạt: {item.isAccess}</p>
                                <p>Email: {item.email}</p>
                                <p>Giọng: {changeVoiceId(item.voice_region)}</p>
                                <p>Trình độ: {changeLevelId(item.level)}</p>

                            </div>
                            <div className='icon_' style={{
                                cursor: 'pointer',

                            }}>
                                <button><i className='bx bxs-edit bx-md'></i></button>
                                <button><i className='bx bx-user-x bx-md'></i></button>
                            </div>
                        </li>
                    </div>
                )
            }
        </ul>
        </div>
        
    )
}
export default userControlView;
