import React, { Component, useState } from 'react'
import './HeaderLayout.scss';

function clear() {
  var a = document.getElementById("inputSearchUser");
  a.value = "";
}
function InputField() {
  // State to store value from the input field
  const [inputValue, setInputValue] = useState("");

  // Input Field handler
  const handleUserInput = (e) => {
    setInputValue(e.target.value);
  };

  // Reset Input Field handler
  const resetInputField = () => {
    setInputValue("");
  };

  return (
    <div>
      {/* Input Field */}
      <input type="text" value={inputValue} onChange={handleUserInput} />

      {/* Reset button */}
      <button onClick={resetInputField}>Reset</button>
    </div>
  );
}
export default class HeaderComponent extends Component {
  
  render() {
    return (
      <div>
        <header>
          <div className="search" id="searchUser">
            <input
              id='inputSearchUser'
              className="search Box"
              type="text"
              placeholder="Tìm theo tên, id, số điện thoại, email, facebook"
            // position="relative"
            ></input>

            <button
              className="search Submit"
              type="submit"
              position="relative"
              onClick={InputField}
            >
              Tìm kiếm
            </button>
            <script>
              
            </script>
            <a href="/addUser" className="newUser">
              Thêm người dùng
            </a>



          </div>
        </header>
      </div>
    )
  }
}
